// è·å–éšæœºå•è¯
function getRandomWords(count, exclude = []) {
    const available = wordBank.filter(word => !exclude.includes(word));
    const shuffled = [...available].sort(() => Math.random() - 0.5);
    return shuffled.slice(0, count);
}

// è·å–æŒ‡å®šç±»åˆ«çš„å•è¯
function getWordsByCategory(category) {
    return wordBank.filter(word => word.category === category);
}

// è·å–æ‰€æœ‰ç±»åˆ«
function getAllCategories() {
    return [...new Set(wordBank.map(word => word.category))];
}
